import React from 'react';
import { MDBIcon } from 'mdbreact';
const MovieListCardItem = (props) => {
  const { movie } = props;

  return (
    <div className="row align-items-center">
      <div class="col-lg-5 col-xl-4">
        <div class="view overlay rounded z-depth-1-half mb-lg-0 mb-4">
          <img class="img-fluid" src={movie.image} alt="Sample image" />
          <a><div class="mask rgba-white-slight"></div></a>
        </div>
      </div>
      <div class="col-lg-7 col-xl-8">
  <h4 class="font-weight-bold mb-3"><strong>#{movie.rank}</strong> | <MDBIcon icon="star" />{movie.imDbRating}</h4>
        <p class="dark-grey-text">{movie.fullTitle}</p>
        <p>Year: {movie.year}</p>
        <p>by <a class="font-weight-bold">Jessica Clark</a>, 19/04/2018</p>
        <a class="btn btn-primary btn-md mx-0 btn-rounded">Read more</a>
      </div>
    </div>
  )
}

export default MovieListCardItem;
