import React from 'react';
import { MDBIcon } from 'mdbreact';
const MovieListCardItem = (props) => {
  const { movie } = props;

  return (
    <div className="row align-items-center mb-2">
      <div className="col-lg-5 col-xl-4">
        <div className="view overlay mb-lg-0 mb-4">
          <img style={{ maxWidth: "200px"}} src={movie.image} alt="Sample image" />
          <a><div className="mask rgba-white-slight"></div></a>
        </div>
      </div>
      <div className="col-lg-7 col-xl-8">
        <h4 className="font-weight-bold mb-3">#{movie.rank}</h4>
        <p className="dark-grey-text">{movie.fullTitle}</p>
        <p>Year: {movie.year}</p>
        <p>by <a className="font-weight-bold">Jessica Clark</a>, 19/04/2018</p>
        <a className="btn btn-primary btn-md mx-0 btn-rounded">Read more</a>
      </div>
      <hr/>
    </div>
  )
}

export default MovieListCardItem;
