import React, { Component } from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';
import axios from "axios";


export default class Register extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fname: "",
            lname: "",
            email: "",
            password: ""
        };
        this.register = this.register.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange = e => {
        this.setState({ [e.target.name]: e.target.value });
    };

    register = e => {
        e.preventDefault();
        const user = {
            firstName: this.state.fname,
            lastName: this.state.lname,
            email: this.state.email,
            password: this.state.password
        };

        axios.post(`localhost:8001/user/signup`, {user})
        .then(res=>{
            console.log(res);
            console.log(res.data);
        })
    
    };

    render() {
        const { fname, lname, email, password } = this.state;
        return (
            <MDBContainer>
                <MDBRow>
                    <MDBCol md="6">
                        <form>
                            <p className="h5 text-center mb-4">Sign up</p>
                            <div className="grey-text">
                                <MDBInput name="fname" onChange={this.handleChange} label="Your Firstname" icon="user" group type="text" validate error="wrong"
                                    success="right" />
                                <MDBInput name="lname" onChange={this.handleChange} label="Your Lastname" icon="user" group type="text" validate error="wrong"
                                    success="right" />
                                <MDBInput name="email" onChange={this.handleChange} label="Your email" icon="envelope" group type="email" validate error="wrong"
                                    success="right" />
                                <MDBInput name="password" onChange={this.handleChange} label="Your password" icon="lock" group type="password" validate />
                            </div>
                            <div className="text-center">
                                <MDBBtn onClick={this.register} color="primary">Register</MDBBtn>
                            </div>
                        </form>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        )
    }
}
