import React, {Component} from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';
import axios from "axios";


export default class Register extends Component{
    
    render() { 
        const {fname,lname,email,password} = this.state;
        return (
            <MDBContainer>
                <MDBRow>
                    <MDBCol md="6">
                        <form>
                            <p className="h5 text-center mb-4">Sign up</p>
                            <div className="grey-text">
                                <MDBInput value={fname} onChange={this.handleChange} label="Your Firstname" icon="user" group type="text" validate error="wrong"
                                    success="right" />
                                <MDBInput  value={lname}  onChange={this.handleChange} label="Your Lastname" icon="user" group type="text" validate error="wrong"
                                    success="right" />
                                <MDBInput value={email}  onChange={this.handleChange} label="Your email" icon="envelope" group type="email" validate error="wrong"
                                    success="right" />
                                <MDBInput value={password}  onChange={this.handleChange} label="Your password" icon="lock" group type="password" validate />
                            </div>
                            <div className="text-center">
                                <MDBBtn type="submit" color="primary">Register</MDBBtn>
                            </div>
                        </form>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        )
    }
}
