import React from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';
import axios from "axios";

export default function Register() {
    React.state = {
        firstName: '',
        lastName: '',
        email: '',
        password: ''
    }
    this.handleChange = this.handleChange.bind(this);
    handleChange = event => {
        event.preventDefault();
        const newUser = {
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            email: this.state.email,
            password: this.state.password
        };

    }

    return (
        <MDBContainer>
            <MDBRow>
                <MDBCol md="6">
                    <form>
                        <p className="h5 text-center mb-4">Sign up</p>
                        <div className="grey-text">
                            <MDBInput firstName="firstName" onChange={this.handleChange} label="Your Firstname" icon="user" group type="text" validate error="wrong"
                                success="right" />
                            <MDBInput lastName="lastName"  onChange={this.handleChange} label="Your Lastname" icon="user" group type="text" validate error="wrong"
                                success="right" />
                            <MDBInput email="email" onChange={this.handleChange} label="Your email" icon="envelope" group type="email" validate error="wrong"
                                success="right" />
                            <MDBInput password="password" onChange={this.handleChange} label="Your password" icon="lock" group type="password" validate />
                        </div>
                        <div className="text-center">
                            <MDBBtn type="submit" color="primary">Register</MDBBtn>
                        </div>
                    </form>
                </MDBCol>
            </MDBRow>
        </MDBContainer>
    )
}
