import React from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBIcon } from 'mdbreact';
import axios from "axios";

export default function Register() {
    const [email, setEmail] = React.useState("");
    const [pass, setPass] = React.useState("");
    
    const submitForm = () => {
        axios.post("http://localhost:3001/user/signup",{ 
            email:email,
            password:pass
        })
        .then((response)=>{
            console.log(response);
        }, (error) => {
            console.log(error);
        });
    };

    return (
        <MDBContainer
            className='d-flex justify-content-center align-items-center'
            style={{ height: '100%', width: '100%', paddingTop: '3rem' }}
        >
            <MDBRow>
                <MDBCol md='6'>
                <MDBCard wide>
                    <MDBCardImage
                        className='view view-cascade gradient-card-header peach-gradient'
                        cascade
                        tag='div'
                    >
                        <h2 className='h2-responsive mb-2'>Title of the news</h2>
                        <p>
                        <MDBIcon icon='calendar-alt' /> 26.07.2017
                        </p>
                    </MDBCardImage>
                    <MDBCardBody cascade className='text-center'>
                        <MDBCardText>
                        Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                        Voluptatibus, ex, recusandae. Facere modi sunt, quod quibusdam
                        dignissimos neque rem nihil ratione est placeat vel, natus non
                        quos laudantium veritatis sequi.Ut enim ad minima veniam, quis
                        nostrum.
                        </MDBCardText>
                        <a
                        href='!#'
                        className='orange-text mt-1 d-flex justify-content-end align-items-center'
                        >
                        <h5 className=''>
                            Read more{' '}
                            <MDBIcon
                            icon='chevron-right'
                            className='ml-2'
                            size='sm'
                            ></MDBIcon>
                        </h5>
                        </a>
                    </MDBCardBody>
                    </MDBCard>
                </MDBCol>
            </MDBRow>
        </MDBContainer>
    )
}
