import React from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBIcon } from 'mdbreact';
import axios from "axios";

export default function Register() {
    const [email, setEmail] = React.useState("");
    const [pass, setPass] = React.useState("");
    
    const submitForm = () => {
        axios.post("http://localhost:3001/user/signup",{ 
            email:email,
            password:pass
        })
        .then((response)=>{
            console.log(response);
        }, (error) => {
            console.log(error);
        });
    };

    return (
        <MDBContainer
            className='d-flex justify-content-center'
            style={{ height: '100%', width: '100%', paddingTop: '3rem' }}
        >
            <MDBRow>
                <MDBCol md='6'>
                <MDBCard wide style={{ width: "22rem" }}>
                <MDBCardImage
            className='view view-cascade gradient-card-header peach-gradient'
            cascade
            tag='div'
          >
            <h2 className='h2-responsive mb-2'>Title of the news</h2>
            <p>
              <MDBIcon icon='calendar-alt' /> 26.07.2017
            </p>
          </MDBCardImage>
                    <MDBCardBody>
                    <MDBCardTitle>Sign Up</MDBCardTitle>
                    <MDBCardText>
                        Some quick example text to build on the card title and make
                        up the bulk of the card&apos;s content.
                    </MDBCardText>
                    <MDBBtn href="#">MDBBtn</MDBBtn>
                    </MDBCardBody>
                </MDBCard>
                </MDBCol>
            </MDBRow>
        </MDBContainer>
    )
}
