import React, { Component, createContext, Fragment } from 'react';
import { MDBContainer } from 'mdbreact';
import Spinner from '../layout/Spinner';
import MovieListCardItem from '../movie/MovieListCardItem';
import { Consumer } from '../../context';
import axios from 'axios';

class MostPopularMovies extends Component {
  state = {
    movie_list: []
  };

  componentDidMount(){
    axios.get(`https://imdb-api.com/en/API/MostPopularMovies/k_54e996be`)
      .then(res => {
        const movie_list = res.data;
        this.setState({ movie_list });
        console(res.data);
      })
      .catch(err => console.log(err));
  }

  render() {
    return (
      
      <ul>
        { (this.state.movie_list.items || []).map(person => <li>{person.id}</li>)}
      </ul>
    )
  }

}

export default MostPopularMovies;
