import React, { Component, Redirect } from "react";
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import Navbar from './components/layout/Navbar';
import Footer from './components/layout/Footer';
import Home from './components/home/Home';
import MostPopularMovies from './components/charts/MostPopularMovies';
import MostPopularTVs from './components/charts/MostPopularTVs';
import BestMovies from './components/charts/BestMovies';
import Register from "./components/register/Register";
import Login from "./components/register/Login";
import Cart from "./components/account/cart";
import Logout from "./components/register/Logout";
import MoviePage from './components/movie/MoviePage'
import axios from "axios";

import { Provider } from './context';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogged: "hi",
      fullname: "James"
    };
  }

  RedirectLoginReg = ({ children, ...props }) => {
    console.log(props);
    return (
      <Route
        path={props.path}
        render={({ location }) =>
          this.state.isLogged === false ? (
            children
          ) : (
            <Redirect
              to={{
                pathname: "/",
                state: { from: location }
              }}
            />
          )
        }
      />
    );
  };

    componentDidMount() {
    this.authListenser();
  }

  authListenser(){
    axios.get("http://localhost:3001/user/isLogin", {
      headers: {
        "x-access-token": localStorage.getItem("token"),
      },
    })
    .then((response) => {
      console.log(response.data.user);
      if(response.data.loggedIn === true){
        this.setState({fullname: response.data.user})
      }
      else{
        this.setState({fullname:""})
      }
    })
  }
  

  render() {
    return (
      <Provider>
        <Router>
          <Navbar 
           render={props => (
            <Navbar { ...props} fullname={this.state.fullname} />
          )} />
          <Switch>
            <Route 
            exact 
            path="/"
            render={props => (
              <Home { ...props} isLogged={this.state.isLogged} />
            )} />
            <Route exact path="/register" component={Register} />
            <Route exact path="/login" component={Login} />
            <Route exact path="/logout" component={Logout} />
            <Route exact path="/account/cart" component={Cart} />
            <Route exact path="/charts/most-popular" component={MostPopularMovies} />
            <Route exact path="/charts/most-popular-tvs" component={MostPopularTVs} />
            <Route exact path="/charts/top-rated-movies" component={BestMovies} />
            <Route exact path="/movie/:id" component={MoviePage} />
          </Switch>
          <Footer />
        </Router>
      </Provider>

    );
  }
}

export default App;
